'use strict';

const Command = require('mysql2/lib/commands/command');
const Packets = require('mysql2/lib/packets');

class CloseStatement extends Command {
  constructor(id) {
    super();
    this.id = id;
  }

  start(packet, connection) {
    connection.writePacket(new Packets.CloseStatement(this.id).toPacket(1));
    return null;
  }
}

module.exports = CloseStatement;
